	protected float[] points = new float[84*3];
	protected boolean _isWireframe = true;
	
	public TextureSphereRotate( int width, int height ) {
		super();

		buildGraphics( width, height );
		
		float radius = width;
		for( int i=0; i < points84.length; i++ ) {
			points[i] = ConvertUtil.stringToFloat( points84[i] ) * radius;
		}
	}
	
	public void newLineMode() {
		_isWireframe = !_isWireframe;
	}
	
	public void update() {
		super.update();
				
		_texture.beginDraw();
		_texture.clear();
		
		_texture.translate( _texture.width * 0.5f, _texture.height * 0.5f );
		_texture.rotateY( P.p.millis() * 0.0007f );
		
		if( _isWireframe == true ) {
			_texture.noFill();
			_texture.strokeWeight(3);
			_texture.stroke( _colorEase.colorInt() );
		} else {
			_texture.noStroke();
			_texture.fill( _colorEase.colorInt() );
		}
		
		_texture.beginShape();
		float size = 10;
		for( int i=0; i < points.length; i+=3 ) {
			float amp = P.p.audioIn.getEqBand( i%512 );
			_texture.pushMatrix();
			_texture.translate(points[i], points[i+1], points[i+2]);
			_texture.box(size * amp, size * amp, size * amp);
			_texture.popMatrix();
		}
		_texture.endShape( PConstants.CLOSE );
		
		_texture.endDraw();
	}
